const e=document.querySelector("#form");let n=0,t=0;e.addEventListener("submit",(r=>{if(r.preventDefault(),""===e.quantity.value)return console.log("Error: Insert a number");let l=parseInt(e.quantity.value),o=e.typeOfQuantity.value,u=e.description.value,a=u.charAt(0).toUpperCase()+u.slice(1),c=document.querySelector("#totalIncome"),i=document.querySelector("#totalExpense"),s=document.querySelector("#balanceTotal");if("income"==o){c.querySelector("ul").insertAdjacentHTML("beforeend",`\n      <li>\n        <h3>\n          ${a}: $${l}\n        </h3>\n      </li>\n    `),n+=l}else{i.querySelector("ul").insertAdjacentHTML("beforeend",`\n      <li>\n        <h3>\n          ${a}: $${l}\n        </h3>\n      </li>\n    `),t+=l}s.innerHTML=`<h2>Total: $${n-t}</h2>`}));
